Property.Value:
BEGIN SET NOCOUNT ON;
DECLARE @APIASVID AS INT; DECLARE @APIID AS INT;
SET @APIASVID = ( SELECT TM_APP_SETTING_CONFIG_VERSION.ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'API') AND TM_APP_SETTING_CONFIG_VERSION.ASV_VERSION = @apiversion ); SET @APIID = ( SELECT API_ID FROM TS_API_SETTING WHERE API_ASV_ID = @APIASVID AND API_KEY = @apikey )
DELETE FROM TS_HISTORY_API_SETTING WHERE HPI_API_ID = @APIID
DELETE FROM TS_API_SETTING WHERE API_ASV_ID = @APIASVID AND API_KEY = @apikey END

BEGIN SET NOCOUNT ON;
DECLARE @APIASVID AS INT;
SET @APIASVID = ( SELECT MAX(TM_APP_SETTING_CONFIG_VERSION.ASV_ID) FROM TM_APP_SETTING_CONFIG_VERSION INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'API') );
DECLARE @DtgId INT = NULL
EXEC @DtgId = Security_Auditing_Create @userName;
IF @DtgId IS NULL OR @DtgId = 0 BEGIN RAISERROR ( 'SP: Security_Auditing_Create has failed!' ,16 ,1 )
RETURN 0 END
INSERT INTO TS_API_SETTING ( API_ASV_ID, API_KEY, API_VALUE, API_DESCRIPTION, API_SENSITIVE_VALUE
) VALUES ( @APIASVID ,@apikey ,@apivalue ,@apidescription ,COALESCE(@apisensitivevalue,0) ); END

BEGIN SET NOCOUNT ON
DECLARE @errorMessage VARCHAR(1300) ,@version_id DECIMAL(10, 2) = NULL ,@config_setting_type_id INT = NULL;
SELECT @config_setting_type_id = cst_id FROM TS_CONFIG_SETTING_TYPE WHERE cst_code = 'API';
IF @config_setting_type_id IS NULL BEGIN SELECT @errorMessage = 'Api setting type not found for code : API';
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END
SELECT @version_id = ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @app_settings_version AND ASV_CST_ID = @config_setting_type_id;
IF @version_id IS NULL AND @app_settings_version is not null BEGIN SELECT @errorMessage = CONCAT ( 'Api setting version not found for version : ' ,@app_settings_version );
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END
declare @versionToFind DECIMAL(10, 2) = NULL;
IF @app_settings_version is null BEGIN DECLARE @max_version_id INT = NULL ,@max_version_number DECIMAL(10, 2) = NULL;
SELECT @max_version_id = max(ASV_ID) ,@max_version_number = ASV_VERSION FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_CST_ID = @config_setting_type_id GROUP BY ASV_ID ,ASV_VERSION;
IF @max_version_id IS NULL BEGIN SELECT @errorMessage = 'Api setting version not found for latest version';
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END ELSE BEGIN set @versionToFind = @max_version_id; END END ELSE BEGIN set @versionToFind = @version_id; END
IF @read_sensitive_value = 1 BEGIN SELECT API_KEY ,API_VALUE ,API_DESCRIPTION ,API_SENSITIVE_VALUE FROM TS_API_SETTING WHERE API_ASV_ID = @versionToFind; END ELSE BEGIN SELECT API_KEY ,CASE WHEN API_SENSITIVE_VALUE=0 THEN API_VALUE ELSE null END AS API_VALUE ,API_DESCRIPTION ,API_SENSITIVE_VALUE FROM TS_API_SETTING WHERE API_ASV_ID = @versionToFind; END
IF @app_settings_version is null begin SELECT @max_version_number AS max_version_number; end else begin SELECT ASV_VERSION AS max_version_number FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_CST_ID = @config_setting_type_id and asv_id = @versionToFind end
END

BEGIN SET NOCOUNT ON;
DECLARE @APIASVID AS INT;
SET @APIASVID = ( SELECT TM_APP_SETTING_CONFIG_VERSION.ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'API') AND TM_APP_SETTING_CONFIG_VERSION.ASV_VERSION = @apiversion );
UPDATE TS_API_SETTING SET API_KEY = COALESCE(@apikeyNew, API_KEY) ,API_VALUE = COALESCE(@apivalue, API_VALUE) ,API_DESCRIPTION = COALESCE(@apidescription, API_DESCRIPTION) ,API_SENSITIVE_VALUE = COALESCE(@apisensitivevalue, API_SENSITIVE_VALUE) WHERE API_ASV_ID = @APIASVID AND API_KEY = @apikey END

BEGIN SET NOCOUNT ON;
DECLARE @APIID AS INT; DECLARE @newkey AS INT;
SET @APIID = ( SELECT MAX(TM_APP_SETTING_CONFIG_VERSION.ASV_ID) FROM TM_APP_SETTING_CONFIG_VERSION INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'API') );
DECLARE @DtgId AS INT = NULL;
EXECUTE @DtgId = Security_Auditing_Create @userName;
IF @DtgId IS NULL OR @DtgId = 0 BEGIN RAISERROR ( 'SP: Security_Auditing_Create has failed!' ,16 ,1 );
RETURN 0; END
SET @newkey = ( SELECT count(*) FROM TS_API_SETTING WHERE API_KEY = @apikey AND API_ASV_ID = ( SELECT MAX(ASV_ID) FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @apiversion ) );
IF @newkey = 0 BEGIN INSERT INTO TS_API_SETTING VALUES ( @APIID ,@apikey ,@apivalue ,@apidescription ,COALESCE(@apisensitivevalue, 0) );
INSERT INTO TS_API_SETTING SELECT @APIID ,API_KEY ,API_VALUE ,API_DESCRIPTION ,API_SENSITIVE_VALUE FROM TS_API_SETTING WHERE API_ASV_ID = ( SELECT DISTINCT ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @apiversion AND ASV_CST_ID = 1 ); END ELSE BEGIN INSERT INTO TS_API_SETTING VALUES ( @APIID ,@apikey ,@apivalue ,@apidescription ,COALESCE(@apisensitivevalue, 0) );
INSERT INTO TS_API_SETTING SELECT @APIID ,API_KEY ,API_VALUE ,API_DESCRIPTION ,API_SENSITIVE_VALUE FROM TS_API_SETTING WHERE API_ASV_ID = ( SELECT DISTINCT ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @apiversion AND ASV_CST_ID = 1 ) AND API_KEY <> @apikey; END END

BEGIN
    SET NOCOUNT ON;
    DECLARE @errorMessage AS VARCHAR (1300), @version_id AS DECIMAL (10, 2) = NULL, @config_setting_type_id AS INT = NULL;
    SELECT @config_setting_type_id = cst_id
    FROM   TS_CONFIG_SETTING_TYPE
    WHERE  cst_code = @config_setting_type;
    IF @config_setting_type_id IS NULL
        BEGIN
            SELECT @errorMessage = CONCAT(@config_setting_type, ' setting type not found for code : ' + @config_setting_type);
            RAISERROR (@errorMessage, 16, 1);
            RETURN 0;
        END
    IF @auto_version = 1
        BEGIN
            SELECT @version_id = max(ASV_ID)
            FROM   TM_APP_SETTING_CONFIG_VERSION
            WHERE  ASV_CST_ID = @config_setting_type_id;
        END
    ELSE
        BEGIN
            SELECT @version_id = ASV_ID
            FROM   TM_APP_SETTING_CONFIG_VERSION
            WHERE  ASV_VERSION = @app_settings_version
                   AND ASV_CST_ID = @config_setting_type_id;
        END
    IF @version_id IS NULL
        BEGIN
            SELECT @errorMessage = CONCAT(@config_setting_type, ' setting version not found for version : ', @app_settings_version);
            RAISERROR (@errorMessage, 16, 1);
            RETURN 0;
        END
    DECLARE @machineIP AS VARCHAR (100) = NULL;
    SELECT @machineIP = CONVERT (VARCHAR (100), CONNECTIONPROPERTY('client_net_address'));
    DECLARE @historyID AS INT = NULL;
    SELECT @historyID = max(HSV_ID)
    FROM   TM_HISTORY_APP_SETTING_CONFIG_VERSION
    WHERE  HSV_ASV_ID = @version_id;
    INSERT  INTO [TM_HISTORY_APP_SETTING_CONFIG_VERSION_DEPLOY] ([HCD_HSV_ID], [HCD_IP_ADDRESS])
    VALUES                                                     (@historyID, @machineIP);
END

CREATE PROCEDURE [dbo].[App_Setting_Deploy_Update]
@app_settings_version DECIMAL (10, 2), @config_setting_type VARCHAR (10), @auto_version BIT=NULL
AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO TM_APP_SETTING_CONFIG_VERSION
	VALUES (
		@version_id
		,@cst_id
		);

	SELECT ASV_VERSION
	FROM TM_APP_SETTING_CONFIG_VERSION
	WHERE ASV_VERSION = @version_id
		AND ASV_CST_ID = @cst_id;
END

BEGIN
	SET NOCOUNT ON;

	DECLARE @ASVID AS INT;




	SET @ASVID = (
			SELECT MAX(TM_APP_SETTING_CONFIG_VERSION.ASV_ID)
			FROM TM_APP_SETTING_CONFIG_VERSION
			INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID
			WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'APP')
			);

	DECLARE @DtgId INT = NULL

	EXEC @DtgId = Security_Auditing_Create @userName;

	IF @DtgId IS NULL
		OR @DtgId = 0
	BEGIN
		RAISERROR (
				'SP: [App_Settings_Migrate_Update] failed'
				,16
				,1
				)

		RETURN 0
	END;



	DISABLE TRIGGER TRIG_TS_APP_SETTING_UPDATE ON TS_APP_SETTING;


	BEGIN TRY
		UPDATE TS_APP_SETTING
		SET APP_VALUE=@appvalue,
		APP_DESCRIPTION=@appdescription
		WHERE APP_KEY=@appkey
		AND APP_ASV_ID=@ASVID;
	END TRY
	BEGIN CATCH
		ENABLE TRIGGER TRIG_TS_APP_SETTING_UPDATE ON TS_APP_SETTING;
		THROW;
	END CATCH;

	ENABLE TRIGGER TRIG_TS_APP_SETTING_UPDATE ON TS_APP_SETTING;

	return @@rowcount
END

