CREATE
	

 PROCEDURE [dbo].[App_Settings_Migrate_Update] @appkey VARCHAR(200)
	,@appvalue VARCHAR(MAX)
	,@appdescription VARCHAR(MAX)
	,@appsensitivevalue BIT = NULL
	,@userName NVARCHAR(256)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @APPID AS INT;

	SET @APPID = (
			SELECT MAX(TM_APP_SETTING_CONFIG_VERSION.ASV_ID)
			FROM TM_APP_SETTING_CONFIG_VERSION
			INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID
			WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'APP')
			);

	DECLARE @DtgId INT = NULL

	EXEC @DtgId = Security_Auditing_Create @userName;

	IF @DtgId IS NULL
		OR @DtgId = 0
	BEGIN
		RAISERROR (
				'SP: Security_Auditing_Create has failed!'
				,16
				,1
				)

		RETURN 0
	END

	INSERT INTO TS_APP_SETTING
	VALUES (
		@APPID
		,@appkey
		,@appvalue
		,@appdescription
		,COALESCE(@appsensitivevalue, 0)
		);
END

BEGIN SET NOCOUNT ON
DECLARE @errorMessage VARCHAR(1300) ,@version_id DECIMAL(10, 2) = NULL ,@config_setting_type_id INT = NULL;
SELECT @config_setting_type_id = cst_id FROM TS_CONFIG_SETTING_TYPE WHERE cst_code = 'APP'
IF @config_setting_type_id IS NULL BEGIN SELECT @errorMessage = 'App setting type not found for code : APP';
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END
SELECT @version_id = ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @app_settings_version AND ASV_CST_ID = @config_setting_type_id;
IF @version_id IS NULL AND @app_settings_version is not null BEGIN SELECT @errorMessage = CONCAT ( 'App setting version not found for version : ' ,@app_settings_version );
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END
declare @versionToFind DECIMAL(10, 2) = NULL;
IF @app_settings_version is null BEGIN DECLARE @max_version_id INT = NULL ,@max_version_number DECIMAL(10, 2) = NULL;
SELECT @max_version_id = max(ASV_ID) ,@max_version_number = ASV_VERSION FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_CST_ID = @config_setting_type_id GROUP BY ASV_ID ,ASV_VERSION;
IF @max_version_id IS NULL BEGIN SELECT @errorMessage = 'App setting version not found for latest version';
RAISERROR ( @errorMessage ,16 ,1 )
RETURN 0 END ELSE BEGIN set @versionToFind = @max_version_id; END END ELSE BEGIN set @versionToFind = @version_id; END
IF @read_sensitive_value = 1 BEGIN SELECT APP_KEY ,APP_VALUE ,APP_DESCRIPTION ,APP_SENSITIVE_VALUE FROM TS_APP_SETTING WHERE APP_ASV_ID = @versionToFind; END ELSE BEGIN SELECT APP_KEY ,CASE WHEN APP_SENSITIVE_VALUE=0 THEN APP_VALUE ELSE null END AS APP_VALUE ,APP_DESCRIPTION ,APP_SENSITIVE_VALUE FROM TS_APP_SETTING WHERE APP_ASV_ID = @versionToFind; END
IF @app_settings_version is null begin SELECT @max_version_number AS max_version_number; end else begin SELECT ASV_VERSION AS max_version_number FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_CST_ID = @config_setting_type_id and asv_id = @versionToFind end END

BEGIN SET NOCOUNT ON;
DECLARE @APPID AS INT; DECLARE @newkey AS INT;
SET @APPID = ( SELECT MAX(TM_APP_SETTING_CONFIG_VERSION.ASV_ID) FROM TM_APP_SETTING_CONFIG_VERSION INNER JOIN TS_CONFIG_SETTING_TYPE ON TS_CONFIG_SETTING_TYPE.CST_ID = TM_APP_SETTING_CONFIG_VERSION.ASV_CST_ID WHERE (TS_CONFIG_SETTING_TYPE.CST_CODE = 'APP') );
DECLARE @DtgId AS INT = NULL;
EXECUTE @DtgId = Security_Auditing_Create @userName;
IF @DtgId IS NULL OR @DtgId = 0 BEGIN RAISERROR ( 'SP: Security_Auditing_Create has failed!' ,16 ,1 );
RETURN 0; END
SET @newkey = ( SELECT count(*) FROM TS_APP_SETTING WHERE APP_KEY = @appkey AND APP_ASV_ID = ( SELECT MAX(ASV_ID) FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @appversion ) );
IF @newkey = 0 BEGIN INSERT INTO TS_APP_SETTING VALUES ( @APPID ,@appkey ,@appvalue ,@appdescription ,COALESCE(@appsensitivevalue, 0) );
INSERT INTO TS_APP_SETTING SELECT @APPID ,APP_KEY ,APP_VALUE ,APP_DESCRIPTION ,APP_SENSITIVE_VALUE FROM TS_APP_SETTING WHERE APP_ASV_ID = ( SELECT DISTINCT ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @appversion AND ASV_CST_ID = 2 ); END ELSE BEGIN INSERT INTO TS_APP_SETTING VALUES ( @APPID ,@appkey ,@appvalue ,@appdescription ,COALESCE(@appsensitivevalue, 0) );
INSERT INTO TS_APP_SETTING SELECT @APPID ,APP_KEY ,APP_VALUE ,APP_DESCRIPTION ,APP_SENSITIVE_VALUE FROM TS_APP_SETTING WHERE APP_ASV_ID = ( SELECT DISTINCT ASV_ID FROM TM_APP_SETTING_CONFIG_VERSION WHERE ASV_VERSION = @appversion AND ASV_CST_ID = 2 ) AND APP_KEY <> @appkey; END END

BEGIN SET NOCOUNT ON;
SELECT DISTINCT GMP_CODE AS GmpCode ,GMP_NAME AS GmpName ,GMP_DESCRIPTION AS GmpDescription ,GLR_NAME AS GlrName ,GLR_CODE AS GlrCode ,GMP_FEATURE_COUNT AS GmpFeatureCount ,MTR_ID ,CASE WHEN MTR_ID IS NULL THEN 0 ELSE 1 END AS HasTable INTO #MtrCls FROM TD_GEOMAP INNER JOIN TD_GEOLAYER ON GMP_GLR_ID = GLR_ID LEFT JOIN TD_MATRIX_DIMENSION ON (@UrlStub + GMP_CODE) = MDM_GEO_URL LEFT JOIN TD_MATRIX ON MDM_MTR_ID = MTR_ID AND MTR_DELETE_FLAG = 0 WHERE GMP_DELETE_FLAG = 0 AND ( @GmpCode IS NULL OR GMP_CODE = @GmpCode ) AND ( @GlrCode IS NULL OR GLR_CODE = @GlrCode )
SELECT GmpCode ,GmpName ,GmpDescription ,GlrName ,GlrCode ,GmpFeatureCount ,sum(HasTable) AS TableCount FROM #MtrCls GROUP BY GmpCode ,GmpName ,GmpDescription ,GlrName ,GlrCode ,GmpFeatureCount END

BEGIN
    SET NOCOUNT ON;
    DECLARE @DtgID AS INT;
    DECLARE @GlrId AS INT;
    SET @DtgID = (SELECT GMP_DTG_ID
                  FROM   TD_GEOMAP
                  WHERE  GMP_CODE = @GmpCode);
    IF @DtgID = 0
       OR @DtgID IS NULL
        BEGIN
            RAISERROR ('Unknown GeoMap Code', 16, 1);
            RETURN 0;
        END
    SET @GlrId = (SELECT GLR_ID
                  FROM   TD_GEOLAYER
                  WHERE  GLR_CODE = @GlrCode
                         AND GLR_DELETE_FLAG = 0);
    IF @GlrId = 0
       OR @GlrId IS NULL
        BEGIN
            RAISERROR ('Unknown Geolayer Code', 16, 1);
            RETURN 0;
        END
    EXECUTE @DtgID = Security_Auditing_Update @DtgID, @CcnUsername;
    UPDATE TD_GEOMAP
    SET    GMP_NAME        = @GmpName,
           GMP_DESCRIPTION = @GmpDescription,
           GMP_GLR_ID      = @GlrId
    WHERE  GMP_CODE = @GmpCode;
    RETURN @@ROWCOUNT;
END

CREATE   PROCEDURE Data_Geomap_Update
@GmpCode NVARCHAR (32), @GmpName NVARCHAR (256), @GmpDescription NVARCHAR (MAX), @CcnUsername NVARCHAR (256), @GlrCode NVARCHAR (256)
AS

BEGIN SET NOCOUNT ON;
DECLARE @DmrId INT DECLARE @errorMessage NVARCHAR(256)
SET @DmrId = ( SELECT DMR_ID FROM TS_DIMENSION_ROLE WHERE DMR_VALUE = @DmrValue )
IF @DmrId IS NULL BEGIN SET @errorMessage = 'SP: Matrix_Dimension_Create - Dimension role not found: ' + @DmrValue
RAISERROR ( @errorMessage ,16 ,1 );
RETURN 0 END
IF ( SELECT COUNT(*) FROM TD_MATRIX_DIMENSION WHERE MDM_MTR_ID = @MtrId AND MDM_CODE = @MdmCode
) > 0 BEGIN SET @errorMessage = 'SP: Matrix_Dimension_Create - Duplicate Dimension Code: ' + @MdmCode
RAISERROR ( @errorMessage ,16 ,1 );
RETURN 0 END
INSERT INTO TD_MATRIX_DIMENSION ( MDM_SEQUENCE ,MDM_CODE ,MDM_VALUE ,MDM_MTR_ID ,MDM_DMR_ID ,MDM_GEO_FLAG ,MDM_GEO_URL ) VALUES ( @MdmSequence ,@MdmCode ,@MdmValue ,@MtrId ,@DmrId ,@MdmGeoFlag ,@MdmGeoUrl )
RETURN @@IDENTITY END

BEGIN
	SET NOCOUNT ON;


	INSERT INTO TD_MATRIX_DATA (
		MTD_MTR_ID
		,MTD_DATA
		)
	VALUES (
		@MtrId
		,@MatrixData
		)

	RETURN @@ROWCOUNT
END

BEGIN SET NOCOUNT ON;
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
DECLARE @maxDate DATETIME
SELECT MTR_CODE MtrCode ,MTR_TITLE MtrTitle ,MTR_NOTE MtrNote ,MTR_OFFICIAL_FLAG MtrOfficialFlag ,MTR_INPUT MtrInput ,MTR_ID MtrId ,CCN_CRT.CCN_USERNAME CcnUsernameCreate ,DHT_CREATE.DHT_DATETIME DtgCreateDatetime ,CCN_UPD.CCN_USERNAME CcnUsernameUpdate ,DHT_UPDATE.DHT_DATETIME DtgUpdateDatetime ,RLS_CODE RlsCode ,RLS_VERSION RlsVersion ,RLS_REVISION RlsRevision ,RLS_LIVE_FLAG RlsLiveFlag ,RLS_LIVE_DATETIME_FROM RlsLiveDatetimeFrom ,RLS_LIVE_DATETIME_TO RlsLiveDatetimeTo ,RLS_EXCEPTIONAL_FLAG RlsExceptionalFlag ,RLS_RESERVATION_FLAG RlsReservationFlag ,RLS_ARCHIVE_FLAG RlsArchiveFlag ,RLS_ANALYTICAL_FLAG RlsAnalyticalFlag ,RLS_EXPERIMENTAL_FLAG RlsExperimentalFlag ,LNG_ISO_CODE LngIsoCode ,LNG_ISO_NAME LngIsoName ,CPR_VALUE CprValue ,CPR_CODE CprCode ,CPR_URL CprUrl ,MDM_VALUE FrqValue ,MDM_CODE FrqCode ,FRM_TYPE FrmType ,FRM_VERSION FrmVersion ,GRP_CODE GrpCode ,GRP_NAME GrpName ,GRP_CONTACT_NAME GrpContactName ,GRP_CONTACT_PHONE GrpContactPhone ,GRP_CONTACT_EMAIL GrpContactEmail ,CMM_CODE CmmCode ,CMM_VALUE CmmValue ,SBJ_CODE SbjCode ,SBJ_VALUE SbjValue ,PRC_CODE PrcCode ,PRC_VALUE PrcValue FROM TD_MATRIX INNER JOIN TD_AUDITING ON DTG_ID = MTR_DTG_ID INNER JOIN TM_AUDITING_HISTORY DHT_CREATE ON DTG_ID = DHT_CREATE.DHT_DTG_ID INNER JOIN TS_AUDITING_TYPE DTP_CREATE ON DTP_CREATE.DTP_ID = DHT_CREATE.DHT_DTP_ID AND DTP_CREATE.DTP_CODE = 'CREATED' INNER JOIN TD_ACCOUNT CCN_CRT ON CCN_CRT.CCN_ID = DHT_CREATE.DHT_CCN_ID LEFT JOIN TM_AUDITING_HISTORY DHT_UPDATE ON DTG_ID = DHT_UPDATE.DHT_DTG_ID AND DHT_UPDATE.DHT_DATETIME = ( SELECT max(DHT_DATETIME) FROM TM_AUDITING_HISTORY WHERE DHT_DTG_ID = DTG_ID ) LEFT JOIN TS_AUDITING_TYPE DTP_UPDATE ON DHT_UPDATE.DHT_DTP_ID = DTP_UPDATE.DTP_ID AND DTP_UPDATE.DTP_CODE = 'UPDATED' LEFT JOIN TD_ACCOUNT CCN_UPD ON CCN_UPD.CCN_ID = DHT_UPDATE.DHT_CCN_ID INNER JOIN TD_RELEASE ON RLS_ID = MTR_RLS_ID AND RLS_CODE = @RlsCode AND RLS_DELETE_FLAG = 0 INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = RLS_GRP_ID INNER JOIN TS_LANGUAGE ON LNG_ID = MTR_LNG_ID AND LNG_DELETE_FLAG = 0 INNER JOIN TS_COPYRIGHT ON CPR_ID = MTR_CPR_ID AND CPR_DELETE_FLAG = 0 INNER JOIN TD_MATRIX_DIMENSION ON MDM_MTR_ID = MTR_ID INNER JOIN TS_DIMENSION_ROLE ON MDM_DMR_ID=DMR_ID AND DMR_CODE='TIME' INNER JOIN TS_FORMAT ON FRM_ID = MTR_FRM_ID INNER JOIN TD_GROUP GP ON RLS_GRP_ID = GP.GRP_ID AND GP.GRP_DELETE_FLAG = 0 LEFT JOIN TD_COMMENT ON CMM_ID = RLS_CMM_ID AND CMM_DELETE_FLAG = 0 LEFT JOIN TD_PRODUCT ON RLS_PRC_ID = PRC_ID AND PRC_DELETE_FLAG = 0 LEFT JOIN TD_SUBJECT ON SBJ_ID = PRC_SBJ_ID AND SBJ_DELETE_FLAG = 0 WHERE MTR_DELETE_FLAG = 0 AND LNG_ISO_CODE=@LngIsoCode END

BEGIN DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
SELECT mtr.MTR_CODE AS MtrCode ,mtr.MTR_TITLE AS MtrTitle ,rls.RLS_CODE AS RlsCode ,lng.LNG_ISO_CODE AS LngIsoCode ,lng.LNG_ISO_NAME AS LngIsoName ,rls.RLS_ID AS RlsID ,cpr.CPR_CODE AS CprCode ,cpr.CPR_VALUE AS CprValue ,MDM_CODE AS FrqCode ,MDM_VALUE AS FrqValue FROM TD_MATRIX mtr INNER JOIN TD_RELEASE rls ON rls.RLS_ID = mtr.MTR_RLS_ID AND rls.RLS_DELETE_FLAG = 0 AND mtr.MTR_DELETE_FLAG = 0 INNER JOIN TS_LANGUAGE lng ON mtr.MTR_LNG_ID = lng.LNG_ID AND mtr.MTR_DELETE_FLAG = 0 AND lng.LNG_DELETE_FLAG = 0 INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = rls.RLS_GRP_ID INNER JOIN TS_COPYRIGHT cpr ON cpr.CPR_ID = mtr.MTR_CPR_ID AND cpr.CPR_DELETE_FLAG = 0 INNER JOIN TD_MATRIX_DIMENSION ON mtr.MTR_ID = MDM_MTR_ID INNER JOIN TS_DIMENSION_ROLE ON MDM_DMR_ID=DMR_ID AND DMR_CODE='TIME' WHERE rls.RLS_CODE = @RlsCode END

BEGIN SET NOCOUNT ON;
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
SELECT DISTINCT MtrCode
FROM ( SELECT mtr.MTR_CODE MtrCode ,mtr.MTR_LNG_ID ,mtrLng.MTR_TITLE MtrTitle
FROM TD_MATRIX mtr LEFT JOIN ( SELECT MTR_CODE ,MTR_TITLE ,MTR_RLS_ID FROM TD_MATRIX WHERE MTR_DELETE_FLAG = 0
) mtrLng ON mtr.MTR_CODE = mtrLng.MTR_CODE AND MTR.MTR_RLS_ID = mtrLng.MTR_RLS_ID INNER JOIN TD_RELEASE ON RLS_ID = mtr.MTR_RLS_ID AND RLS_DELETE_FLAG = 0 INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = RLS_GRP_ID WHERE MTR_DELETE_FLAG = 0 GROUP BY mtr.MTR_CODE ,mtr.MTR_LNG_ID ,mtrLng.MTR_TITLE ) q
ORDER BY q.MtrCode END

BEGIN SET NOCOUNT ON;
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
SELECT LNG_ISO_CODE LngIsoCode ,LNG_ISO_NAME LngIsoName FROM TD_MATRIX INNER JOIN TD_RELEASE ON RLS_ID = MTR_RLS_ID AND RLS_CODE = @RlsCode AND RLS_DELETE_FLAG = 0 INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = RLS_GRP_ID INNER JOIN TS_LANGUAGE ON LNG_ID = MTR_LNG_ID AND LNG_DELETE_FLAG = 0 WHERE MTR_DELETE_FLAG = 0 END

BEGIN SET NOCOUNT ON;
UPDATE TD_MATRIX SET MTR_DATA_FLAG =COALESCE (@MtrDataFlag,MTR_DATA_FLAG) ,MTR_INPUT=COALESCE(@MtrInput,MTR_INPUT) WHERE MTR_ID = @MtrId
RETURN @@rowcount END

BEGIN SET NOCOUNT ON;
UPDATE TD_MATRIX SET MTR_DATA_FLAG = @MtrDataFlag WHERE MTR_ID = @MtrId
RETURN @@rowcount END

DECLARE @cursor CURSOR;
DECLARE @MtrId INT;
DECLARE @Title AS NVARCHAR (MAX);
DECLARE @Contents AS NVARCHAR (256);
DECLARE @Dimensions AS NVARCHAR (256);
DECLARE @Time AS NVARCHAR (256);
DECLARE @StartValue AS NVARCHAR (256);
DECLARE @FinishValue AS NVARCHAR (256);
CREATE TABLE #TITLES (MtrId INT, MtrTitle NVARCHAR (MAX)) 
BEGIN
	SET @Placeholder = ' ' + @Placeholder + ' ';
    SET @cursor = CURSOR FOR
    select [Key], [Value]
	from @MatrixMap   

    OPEN @cursor 
    FETCH NEXT FROM @cursor 
    INTO @MtrId, @Contents

    WHILE @@FETCH_STATUS = 0
		BEGIN
		SET @Time = (SELECT MDM_VALUE
					 FROM   TD_MATRIX_DIMENSION
					 WHERE  MDM_MTR_ID = @MtrId
							AND MDM_DMR_ID = 2);
        SET @Dimensions = ''
		SELECT @Dimensions = @Dimensions + MDM_VALUE + ', '
		FROM   TD_MATRIX_DIMENSION
		WHERE  MDM_MTR_ID = @MtrId
			   AND MDM_DMR_ID = 3;
		SET @StartValue = (SELECT   TOP (1) DMT_VALUE
						   FROM     TD_DIMENSION_ITEM
						   WHERE    DMT_MDM_ID = (SELECT MDM_ID
												  FROM   TD_MATRIX_DIMENSION
												  WHERE  MDM_MTR_ID = @MtrId
														 AND MDM_DMR_ID = 2)
						   ORDER BY DMT_VALUE DESC);
		SET @FinishValue = (SELECT   TOP (1) DMT_VALUE
							FROM     TD_DIMENSION_ITEM
							WHERE    DMT_MDM_ID = (SELECT MDM_ID
												   FROM   TD_MATRIX_DIMENSION
												   WHERE  MDM_MTR_ID = @MtrId
														  AND MDM_DMR_ID = 2)
							ORDER BY DMT_VALUE ASC);
		SET @Dimensions = SUBSTRING(@Dimensions, 1, len(@Dimensions)-1)
		IF @StartValue = @FinishValue
			BEGIN
				SET @Title = @Contents + @Placeholder + @Dimensions + ' and ' + @Time + ' ' + @StartValue;
			END
		ELSE
			BEGIN
				SET @Title = @Contents + @Placeholder + @Dimensions + ' and ' + @Time + ' ' + @StartValue + @Placeholder + @FinishValue;
			END 
		INSERT INTO #TITLES VALUES(@MtrId, @Title)
		FETCH NEXT FROM @cursor INTO @MtrId, @Contents
    END; 
    CLOSE @cursor;
    DEALLOCATE @cursor;
	SELECT * from #TITLES
END

CREATE     PROCEDURE [dbo].[Data_Read_Title_Update]
@Placeholder NVARCHAR (256), @MatrixMap KeyValueVarcharAttribute READONLY
AS

BEGIN SET NOCOUNT ON;
SELECT rls.RLS_CODE AS RlsCode ,rls.RLS_VERSION AS RlsVersion ,rls.RLS_REVISION AS RlsRevision ,rls.RLS_LIVE_FLAG AS RlsLiveFlag ,rls.RLS_LIVE_DATETIME_FROM AS RlsLiveDatetimeFrom ,rls.RLS_LIVE_DATETIME_TO AS RlsLiveDatetimeTo ,rls.RLS_EXCEPTIONAL_FLAG AS RlsExceptionalFlag ,rls.RLS_RESERVATION_FLAG AS RlsReservationFlag ,rls.RLS_ARCHIVE_FLAG AS RlsArchiveFlag ,rls.RLS_ANALYTICAL_FLAG AS RlsAnalyticalFlag ,rls.RLS_EXPERIMENTAL_FLAG AS RlsExperimentalFlag ,mtr.MTR_CODE AS MtrCode ,GRP_CODE GrpCode ,GRP_NAME GrpName ,GRP_CONTACT_NAME GrpContactName ,GRP_CONTACT_PHONE GrpContactPhone ,GRP_CONTACT_EMAIL GrpContactEmail ,CMM_CODE CmmCode ,CMM_VALUE CmmValue ,SBJ_CODE SbjCode ,SBJ_VALUE SbjValue ,PRC_CODE PrcCode ,PRC_VALUE PrcValue ,LNG_ISO_CODE LngIsoCode FROM TD_RELEASE rls INNER JOIN VW_RELEASE_LIVE_NOW ON VRN_RLS_ID = rls.RLS_ID INNER JOIN TD_MATRIX mtr ON rls.RLS_ID = mtr.MTR_RLS_ID AND mtr.MTR_DELETE_FLAG = 0 AND rls.RLS_DELETE_FLAG = 0 AND VRN_MTR_ID = mtr.MTR_ID INNER JOIN TD_GROUP ON RLS_GRP_ID = GRP_ID AND GRP_DELETE_FLAG = 0 INNER JOIN TS_LANGUAGE on MTR_LNG_ID=LNG_ID and LNG_DELETE_FLAG=0 LEFT JOIN TD_COMMENT ON CMM_ID = RLS_CMM_ID AND CMM_DELETE_FLAG = 0 LEFT JOIN TD_PRODUCT ON RLS_PRC_ID = PRC_ID AND PRC_DELETE_FLAG = 0 LEFT JOIN TD_SUBJECT ON SBJ_ID = PRC_SBJ_ID AND SBJ_DELETE_FLAG = 0
WHERE mtr.MTR_CODE = ( SELECT DISTINCT MTR_CODE FROM TD_MATRIX mtr2 INNER JOIN TD_RELEASE rls2 ON rls2.RLS_ID = mtr2.MTR_RLS_ID AND rls2.RLS_DELETE_FLAG = 0 AND mtr2.MTR_DELETE_FLAG = 0 INNER JOIN TS_LANGUAGE ON mtr2.MTR_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 WHERE ( ( @MtrCode IS NULL AND @RlsCode IS NOT NULL AND rls2.RLS_CODE = @RlsCode ) OR ( @RlsCode IS NULL AND @MtrCode IS NOT NULL AND mtr2.MTR_CODE = @MtrCode ) )
) END

CREATE PROCEDURE [dbo].[Data_Release_ReadLatestIgnoreCancelled] 
	@MtrCode NVARCHAR(256)
AS

BEGIN SET NOCOUNT ON;
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
DECLARE @LngId INT DECLARE @LngIdDefault INT
SET @LngId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCode AND LNG_DELETE_FLAG = 0 ) SET @LngIdDefault = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCodeDefault AND LNG_DELETE_FLAG = 0 )
SELECT DISTINCT q.MtrCode ,q.RlsCode ,q.RlsAnalyticalFlag ,q.RlsArchiveFlag ,q.RlsExperimentalFlag ,q.RlsLiveFlag ,q.RlsLiveDatetimeTo ,q.RlsReservationFlag ,q.RlsVersion ,q.RlsRevision ,q.RlsExceptionalFlag ,q.RlsLiveDatetimeFrom ,q.MtrTitle ,q.RqsValue ,q.RqsCode ,q.RspValue ,q.RspCode ,q.SgnValue ,q.SgnCode FROM ( SELECT mtr.MTR_CODE MtrCode ,RLS_CODE RlsCode ,RLS_ANALYTICAL_FLAG RlsAnalyticalFlag ,RLS_ARCHIVE_FLAG RlsArchiveFlag ,RLS_EXPERIMENTAL_FLAG RlsExperimentalFlag ,RLS_LIVE_FLAG RlsLiveFlag ,RLS_LIVE_DATETIME_TO RlsLiveDatetimeTo ,RLS_RESERVATION_FLAG RlsReservationFlag ,RLS_REVISION RlsRevision ,RLS_VERSION RlsVersion ,RLS_EXCEPTIONAL_FLAG RlsExceptionalFlag ,RLS_LIVE_DATETIME_FROM RlsLiveDatetimeFrom ,coalesce(mtrLng.MTR_TITLE, mtr.MTR_TITLE) MtrTitle ,RQS_VALUE RqsValue ,mtr.MTR_LNG_ID ,RSP_VALUE RspValue ,SGN_VALUE SgnValue ,RQS_CODE RqsCode ,RSP_CODE RspCode ,SGN_CODE SgnCode FROM TD_MATRIX mtr LEFT JOIN ( SELECT MTR_CODE ,MTR_TITLE ,MTR_RLS_ID FROM TD_MATRIX WHERE MTR_DELETE_FLAG = 0 AND MTR_LNG_ID = @LngId ) mtrLng ON mtr.MTR_CODE = mtrLng.MTR_CODE AND MTR.MTR_RLS_ID = mtrLng.MTR_RLS_ID INNER JOIN TD_RELEASE ON RLS_ID = mtr.MTR_RLS_ID AND RLS_DELETE_FLAG = 0 LEFT JOIN TD_WORKFLOW_REQUEST ON WRQ_RLS_ID = RLS_ID and WRQ_CURRENT_FLAG=1 and WRQ_DELETE_FLAG=0 LEFT JOIN TS_REQUEST ON WRQ_RQS_ID = RQS_ID LEFT JOIN TD_WORKFLOW_RESPONSE ON WRQ_ID = WRS_WRQ_ID LEFT JOIN TS_RESPONSE ON WRS_RSP_ID = RSP_ID LEFT JOIN TD_WORKFLOW_SIGNOFF ON WRS_ID = WSG_WRS_ID LEFT JOIN TS_SIGNOFF ON WSG_SGN_ID = SGN_ID INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = RLS_GRP_ID WHERE MTR_DELETE_FLAG = 0 AND mtr.MTR_CODE = @MtrCode AND mtr.MTR_LNG_ID IN ( @LngId ,@LngIdDefault )
) q ORDER BY q.RlsVersion ,q.RlsRevision END

BEGIN
    SET NOCOUNT ON;
    DECLARE @DtgID AS INT;
    DECLARE @eMessage AS VARCHAR (256);
    DECLARE @updateCount AS INT;
    DECLARE @PrcID AS INT;
    SET @PrcID = (SELECT PRC_ID
                  FROM   TD_PRODUCT
                  WHERE  PRC_CODE = @PrcCode
                         AND PRC_DELETE_FLAG = 0);
    SET @DtgID = (SELECT RLS_DTG_ID
                  FROM   TD_RELEASE
                  WHERE  RLS_CODE = @RlsCode
                         AND RLS_DELETE_FLAG = 0);
    DECLARE @RlsCmmId AS INT
    SET @RlsCmmId=NULL
    IF @RlsCmmCode IS NOT NULL AND @RlsCode>0
    BEGIN
        SET @RlsCmmId=(SELECT TOP 1 CMM_ID FROM TD_COMMENT WHERE CMM_CODE=@RlsCmmCode )
    END
    IF @DtgID = 0
       OR @DtgID IS NULL
        BEGIN
            RETURN 0;
        END
    UPDATE TD_RELEASE
    SET    RLS_VERSION            = @RlsVersion,
           RLS_REVISION           = @RlsRevision,
           RLS_LIVE_FLAG          = @RlsLiveFlag,
           RLS_LIVE_DATETIME_FROM = @RlsLiveDatetimeFrom,
           RLS_LIVE_DATETIME_TO   = @RlsLiveDatetimeTo,
           RLS_EXCEPTIONAL_FLAG   = @RlsExceptionalFlag,
           RLS_RESERVATION_FLAG   = @RlsReservationFlag,
           RLS_ARCHIVE_FLAG       = @RlsArchiveFlag,
           RLS_ANALYTICAL_FLAG    = @RlsAnalyticalFlag,
           RLS_EXPERIMENTAL_FLAG  = @RlsExperimentalFlag,
           RLS_PRC_ID             = CASE WHEN @PrcID > 0 THEN @PrcID ELSE NULL END,
           RLS_CMM_ID             = CASE WHEN @RlsCmmId IS NOT NULL THEN @RlsCmmId ELSE RLS_CMM_ID END
    WHERE  RLS_CODE = @RlsCode
           AND RLS_DELETE_FLAG = 0;
    SET @updateCount = @@ROWCOUNT;
    IF @updateCount > 0
        BEGIN
            DECLARE @AuditUpdateCount AS INT;
            EXECUTE @AuditUpdateCount = Security_Auditing_Update @DtgID, @CcnUsername;
            IF @AuditUpdateCount = 0
                BEGIN
                    SET @eMessage = 'Error creating entry in TD_AUDITING for Release update:' + CAST (isnull(@RlsCode, 0) AS VARCHAR) + ' DTG ID: ' + @DtgID;
                    RAISERROR (@eMessage, 16, 1);
                    RETURN;
                END
        END
    IF @PrcCode IS NOT NULL
        BEGIN
            DECLARE @ReleaseId AS INT;
            DECLARE @ProductId AS INT;
            DECLARE @Count AS INT;
            SELECT @ReleaseId = RLS_ID
            FROM   TD_RELEASE
            WHERE  RLS_CODE = @RlsCode;
            SELECT @ProductId = PRC_ID
            FROM   TD_PRODUCT
            WHERE  PRC_CODE = @PrcCode;
            SELECT @Count = COUNT(*)
            FROM   TM_RELEASE_PRODUCT
            WHERE  RPR_RLS_ID = @ReleaseId
                   AND RPR_PRC_ID = @ProductId
                   AND RPR_DELETE_FLAG = 0;
            IF @Count > 0
                BEGIN
                    DECLARE @auditId AS INT = NULL;
                    EXECUTE @auditId = Security_Auditing_Create @CcnUsername;
                    IF @auditId IS NULL
                       OR @auditId = 0
                        BEGIN
                            RAISERROR ('SP: Security_Auditing_Create has failed!', 16, 1);
                            RETURN 0;
                        END
                    UPDATE TM_RELEASE_PRODUCT
                    SET    RPR_DELETE_FLAG = 1,
                           RPR_DTG_ID      = @auditId
                    WHERE  RPR_RLS_ID = @ReleaseId
                           AND RPR_PRC_ID = @ProductId
                           AND RPR_DELETE_FLAG = 0;
                END
        END
    RETURN @updateCount;
END

CREATE   PROCEDURE Data_Release_Update
@CcnUsername NVARCHAR (256), @RlsCode INT, @RlsVersion INT, @RlsRevision INT, @RlsLiveFlag BIT, @RlsLiveDatetimeFrom DATETIME=NULL, @RlsLiveDatetimeTo DATETIME=NULL, @RlsExceptionalFlag BIT, @RlsReservationFlag BIT, @RlsArchiveFlag BIT, @RlsExperimentalFlag BIT, @RlsAnalyticalFlag BIT, @PrcCode NVARCHAR (32)=NULL, @RlsCmmCode INT=NULL
AS

CREATE     PROCEDURE [dbo].[Data_ReleaseProduct_Create]
@ReleaseCode NVARCHAR (256), @ProductCode NVARCHAR (256), @userName NVARCHAR (256)
AS

BEGIN SET NOCOUNT ON;
DECLARE @auditId AS INT = NULL; SELECT @auditId = RPR_DTG_ID FROM TM_RELEASE_PRODUCT WHERE RPR_RLS_ID = ( SELECT RLS_ID FROM TD_RELEASE WHERE RLS_CODE = @ReleaseCode AND RLS_DELETE_FLAG=0 ) AND RPR_PRC_ID = ( SELECT PRC_ID FROM TD_PRODUCT WHERE PRC_CODE = @ProductCode AND PRC_DELETE_FLAG =0 ) AND RPR_DELETE_FLAG = 0
EXECUTE @auditId = Security_Auditing_Delete @auditId, @userName;
IF @auditId IS NULL OR @auditId = 0 BEGIN RAISERROR ( 'SP: Security_Auditing_Delete has failed!' ,16 ,1 );
RETURN 0; END
UPDATE TM_RELEASE_PRODUCT SET RPR_DELETE_FLAG = 1 ,RPR_DTG_ID = @auditId WHERE RPR_RLS_ID = ( SELECT RLS_ID FROM TD_RELEASE WHERE RLS_CODE = @ReleaseCode AND RLS_DELETE_FLAG=0 ) AND RPR_PRC_ID = ( SELECT PRC_ID FROM TD_PRODUCT WHERE PRC_CODE = @ProductCode AND PRC_DELETE_FLAG =0 ) AND RPR_DELETE_FLAG = 0
RETURN @@ROWCOUNT; END

BEGIN SET NOCOUNT ON;
SELECT PRC_CODE AS PrcCode, PRC_VALUE AS PrcValue, SBJ_CODE AS SbjCode, SBJ_VALUE AS SbjValue FROM (SELECT TD_PRODUCT.PRC_ID, TD_PRODUCT.PRC_CODE, SBJ_CODE, COALESCE (PLG_Value, PRC_VALUE) AS PRC_VALUE, COALESCE (SLG_VALUE, SBJ_VALUE) AS SBJ_VALUE, PRC_DELETE_FLAG, RLS_DELETE_FLAG FROM TD_PRODUCT INNER JOIN [TD_SUBJECT] ON SBJ_ID = PRC_SBJ_ID AND SBJ_DELETE_FLAG = 0 LEFT OUTER JOIN (SELECT PRC_CODE, COUNT(*) AS mtrCount FROM (SELECT DISTINCT PRC_CODE, MTR_CODE FROM TD_PRODUCT INNER JOIN TD_RELEASE ON PRC_ID = RLS_PRC_ID AND PRC_DELETE_FLAG = 0 AND RLS_DELETE_FLAG = 0 INNER JOIN TD_MATRIX ON MTR_RLS_ID = RLS_ID AND MTR_DELETE_FLAG = 0) AS prcInner GROUP BY PRC_CODE) AS prcMtrCounter ON TD_PRODUCT.PRC_CODE = prcMtrCounter.PRC_CODE LEFT OUTER JOIN (SELECT SLG_SBJ_ID, SLG_VALUE FROM TD_SUBJECT_LANGUAGE INNER JOIN TS_LANGUAGE ON SLG_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode) AS INNER_SLG ON SBJ_ID = INNER_SLG.SLG_SBJ_ID LEFT OUTER JOIN (SELECT prg.PLG_PRC_ID, prg.PLG_VALUE FROM TD_PRODUCT_LANGUAGE AS prg INNER JOIN TS_LANGUAGE ON prg.PLG_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode) AS INNER_PRG ON PRC_ID = INNER_PRG.PLG_PRC_ID LEFT OUTER JOIN TD_RELEASE ON RLS_PRC_ID = PRC_ID AND RLS_DELETE_FLAG = 0 AND RLS_id IN (SELECT VRN_RLS_ID FROM VW_RELEASE_LIVE_NOW)) AS INNER_SELECT LEFT OUTER JOIN TD_MATRIX ON MTR_RLS_ID = (SELECT RLS_ID FROM TD_RELEASE WHERE RLS_CODE = @ReleaseCode)
WHERE PRC_ID IN (SELECT RPR_PRC_ID FROM TM_RELEASE_PRODUCT WHERE RPR_RLS_ID = (SELECT RLS_ID FROM TD_RELEASE WHERE RLS_CODE = @ReleaseCode) AND RPR_DELETE_FLAG = 0) group by MTR_CODE, PRC_CODE, PRC_VALUE, SBJ_CODE, SBJ_VALUE END

CREATE   PROCEDURE [dbo].[Data_ReleaseProduct_Read]
@ReleaseCode INT, @LngIsoCode CHAR (2)=NULL
AS

BEGIN SET NOCOUNT ON;
DECLARE @GroupTable TABLE (Groupie NVARCHAR(MAX)); DECLARE @ReasonTable TABLE (Reason NVARCHAR(MAX));
IF @GrpCode IS NOT NULL BEGIN INSERT INTO @GroupTable (Groupie) (SELECT value FROM STRING_SPLIT(@GrpCode, ',')); END
IF @RsnCode IS NOT NULL BEGIN INSERT INTO @ReasonTable (Reason) (SELECT value FROM STRING_SPLIT(@RsnCode, ',')); END
SELECT DISTINCT RSN_CODE AS RsnCode ,RSN_VALUE_EXTERNAL AS RsnValueExternal ,RSN_VALUE_INTERNAL AS RsnValueInternal ,GRP_CODE AS GrpCode ,GRP_NAME AS GrpName ,TD_MATRIX.MTR_CODE AS MtrCode ,TD_MATRIX.MTR_TITLE AS MtrTitle ,MDM_CODE AS FrqCode ,MDM_VALUE AS FrqValue ,RLS_VERSION AS RlsVersion ,RLS_REVISION AS RlsRevision ,MTR_NOTE AS MtrNote ,RLS_EXCEPTIONAL_FLAG AS RlsExceptionalFlag ,RLS_LIVE_DATETIME_FROM AS RlsLiveDatetimeFrom ,RLS_LIVE_DATETIME_To AS RlsLiveDatetimeTo FROM TD_RELEASE LEFT JOIN TM_REASON_RELEASE ON RSR_RLS_ID = RLS_ID AND RSR_DELETE_FLAG = 0 LEFT JOIN TS_REASON ON RSR_RSN_ID = RSN_ID INNER JOIN TD_MATRIX ON MTR_RLS_ID = RLS_ID INNER JOIN TS_LANGUAGE ON MTR_LNG_ID = LNG_ID AND LNG_ISO_CODE = @LngIsoCode INNER JOIN TD_GROUP ON RLS_GRP_ID = GRP_ID INNER JOIN TD_MATRIX_DIMENSION ON MDM_MTR_ID = MTR_ID INNER JOIN TS_DIMENSION_ROLE ON MDM_DMR_ID = DMR_ID WHERE ( @GrpCode IS NULL OR ( GRP_CODE IN ( SELECT Groupie FROM @GroupTable ) ) ) AND ( @RsnCode IS NULL OR ( RSN_CODE IN ( SELECT Reason FROM @ReasonTable ) ) ) AND ( RLS_LIVE_DATETIME_FROM BETWEEN @StartDate AND @EndDate ) AND DMR_CODE = 'TIME' AND RLS_LIVE_FLAG = 1;
RETURN; END

BEGIN SET NOCOUNT ON;
DECLARE @MtrID AS INT; DECLARE @FrmID AS INT;
SELECT @MtrID = MTR_ID FROM TD_MATRIX INNER JOIN VW_RELEASE_LIVE_NOW ON MTR_ID = VRN_MTR_ID INNER JOIN TS_LANGUAGE ON LNG_ID = MTR_LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode AND MTR_CODE = @matrix;
IF @MtrID IS NULL BEGIN RETURN 0 END
SELECT @FrmID = FRM_ID FROM TS_FORMAT WHERE @FrmType IS NOT NULL AND @FrmVersion IS NOT NULL AND FRM_DIRECTION = 'DOWNLOAD' AND FRM_TYPE = @FrmType AND FRM_VERSION = @FrmVersion;
IF @FrmID IS NULL BEGIN RETURN 0 END
INSERT INTO TD_ANALYTIC ( NLT_MTR_ID ,NLT_MASKED_IP ,NLT_OS ,NLT_BROWSER ,NLT_BOT_FLAG ,NLT_REFERER ,NLT_M2M_FLAG ,NLT_WIDGET ,NLT_DATE ,NLT_USER_FLAG ,NLT_FRM_ID ,NLT_LNG_ISO_CODE ) VALUES ( @MtrID ,@NltMaskedIp ,@NltOs ,@NltBrowser ,@NltBotFlag ,@NltReferer ,@NltM2m ,coalesce(@NltWidget,0) ,@NltDate ,@NltUser ,@FrmID ,@EnvironmentLngIsoCode );
RETURN @@identity; END

