CREATE   PROCEDURE Security_Api_Trace_Read
@StartDate DATETIME, @EndDate DATETIME, @Const_AUTHENTICATED NVARCHAR (32), @Const_REGISTERED NVARCHAR (32), @Const_ANONYMOUS NVARCHAR (32), @Const_ANY NVARCHAR (32), @AuthenticationType VARCHAR (32)=NULL, @TrcUsername NVARCHAR (256)=NULL, @TrcIp VARCHAR (15)=NULL
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @CcnId AS INT = NULL;
    DECLARE @errorMessage AS VARCHAR (256) = NULL;
    DECLARE @DtgId AS INT = NULL;
    SELECT @CcnId = CCN_ID
    FROM   TD_ACCOUNT
    WHERE  CCN_USERNAME = @userName
           AND CCN_DELETE_FLAG = 0;
    IF @CcnId IS NULL
        BEGIN
            SET @errorMessage = 'SP: Security_Auditing_Create - userName not found: ' + CAST (@userName AS VARCHAR);
            RAISERROR (@errorMessage, 16, 1);
            RETURN 0;
        END
    INSERT  INTO TD_AUDITING
    DEFAULT VALUES;
    SET @DtgId = @@IDENTITY;
    BEGIN TRY
        INSERT  INTO TM_AUDITING_HISTORY (DHT_DTG_ID, DHT_DTP_ID, DHT_CCN_ID, DHT_DATETIME)
        VALUES                          (@DtgId, (SELECT DTP_ID
                                                  FROM   TS_AUDITING_TYPE
                                                  WHERE  DTP_CODE = 'CREATED'), @CcnId, GETDATE());
    END TRY
    BEGIN CATCH
        RETURN NULL;
    END CATCH
    RETURN @DtgId;
END

CREATE   PROCEDURE Security_Auditing_Create
@userName NVARCHAR (256)
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @eMessage AS VARCHAR (256);
    DECLARE @recordCount AS INT;
    DECLARE @DeleteCcnID AS INT = NULL;
    SET @DeleteCcnID = (SELECT CCN_ID
                        FROM   TD_ACCOUNT
                        WHERE  CCN_USERNAME = @DeleteCcnUsername
                               AND CCN_DELETE_FLAG = 0);
    IF @DeleteCcnID IS NULL
       OR @DeleteCcnID = 0
        BEGIN
            SET @eMessage = 'Error amending audit record - no account record found for @DeleteCcnUsername: ' + CAST (isnull(@DeleteCcnUsername, 0) AS VARCHAR);
            RAISERROR (@eMessage, 16, 1);
            RETURN 0;
        END
    SET @recordCount = (SELECT count(*)
                        FROM   TD_AUDITING
                        WHERE  DTG_ID = @DtgID);
    IF @recordCount IS NULL
       OR @recordCount = 0
        BEGIN
            SET @eMessage = 'Error amending audit record - no create audit record found for DtgID: ' + CAST (isnull(@DtgID, 0) AS VARCHAR);
            RAISERROR (@eMessage, 16, 1);
            RETURN 0;
        END
    INSERT  INTO TM_AUDITING_HISTORY (DHT_DTG_ID, DHT_DTP_ID, DHT_CCN_ID, DHT_DATETIME)
    VALUES                          (@DtgID, (SELECT DTP_ID
                                              FROM   TS_AUDITING_TYPE
                                              WHERE  DTP_CODE = 'DELETED'), @DeleteCcnID, GETDATE());
    RETURN @@ROWCOUNT;
END

CREATE   PROCEDURE Security_Auditing_Delete
@DtgID INT, @DeleteCcnUsername NVARCHAR (256)
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @eMessage AS VARCHAR (256);
    DECLARE @recordCount AS INT;
    DECLARE @UpdateCcnID AS INT = NULL;
    DECLARE @DhtId AS INT = NULL;
    SET @UpdateCcnID = (SELECT CCN_ID
                        FROM   TD_ACCOUNT
                        WHERE  CCN_USERNAME = @UpdateCcnUsername
                               AND CCN_DELETE_FLAG = 0);
    IF @UpdateCcnID IS NULL
       OR @UpdateCcnID = 0
        BEGIN
            SET @eMessage = 'Error amending audit record - no account record found for @UpdateCcnID: ' + CAST (isnull(@UpdateCcnUsername, 0) AS VARCHAR);
            RAISERROR (@eMessage, 16, 1);
            RETURN 0;
        END
    SET @DhtId = (SELECT count(*)
                  FROM   TD_AUDITING
                  WHERE  DTG_ID = @DtgID);
    IF @DhtId IS NULL
       OR @recordCount = 0
        BEGIN
            SET @DhtId = 'Error amending audit record - no create audit record found for DtgID: ' + CAST (isnull(@DtgID, 0) AS VARCHAR);
            RAISERROR (@eMessage, 16, 1);
            RETURN 0;
        END
    INSERT  INTO TM_AUDITING_HISTORY (DHT_DTG_ID, DHT_DTP_ID, DHT_CCN_ID, DHT_DATETIME)
    VALUES                          (@DtgID, (SELECT DTP_ID
                                              FROM   TS_AUDITING_TYPE
                                              WHERE  DTP_CODE = 'UPDATED'), @UpdateCcnID, GETDATE());
    RETURN @@ROWCOUNT;
END

CREATE   PROCEDURE Security_Auditing_Update
@DtgID INT, @UpdateCcnUsername VARCHAR (256)
AS

BEGIN
	SET NOCOUNT ON;

	SELECT TABLE_NAME
	FROM Information_Schema.tables
	WHERE TABLE_TYPE = 'BASE TABLE';
END

BEGIN SET NOCOUNT ON;
DECLARE @FRAGMENTATION_LIMIT AS INT; DECLARE @TableName AS NVARCHAR(128); DECLARE @MIN_ROW_COUNT AS INT;
SET @FRAGMENTATION_LIMIT = 51;
INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_MESSAGE ) VALUES ( getdate() ,'0' ,'INFO' ,'SQL SERVER AGENT' ,'Security_Database_ReorganizeFragmented' ,'Starting defragmentation' );
DECLARE table_cursor CURSOR FOR SELECT DISTINCT OBJECT_NAME(ips.OBJECT_ID) AS 'table' FROM sys.dm_db_index_physical_stats(DB_ID(), NULL, NULL, NULL, 'SAMPLED') AS ips INNER JOIN sys.indexes AS i ON ips.object_id = i.object_id AND ips.index_id = i.index_id AND ips.avg_fragmentation_in_percent > @FRAGMENTATION_LIMIT;
OPEN table_cursor;
FETCH NEXT FROM table_cursor INTO @TableName;
INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_MESSAGE ) VALUES ( getdate() ,'0' ,'INFO' ,'SQL SERVER AGENT' ,'Security_Database_ReorganizeFragmented' ,'Starting index defragmentation for ' + CONVERT(VARCHAR(256), @@CURSOR_ROWS) + ' tables' );
WHILE @@FETCH_STATUS = 0 BEGIN BEGIN TRY EXECUTE Security_Database_UpdateStatistic @TableName;
INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_MESSAGE ) VALUES ( getdate() ,'0' ,'INFO' ,'SQL SERVER AGENT' ,'Security_Database_ReorganizeFragmented' ,'INDEXES DEFRAGMENTED for ' + @TableName );
SELECT @TableName;
FETCH NEXT FROM table_cursor INTO @TableName; END TRY
BEGIN CATCH INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_MESSAGE ) VALUES ( getdate() ,'0' ,'INFO' ,'SQL SERVER AGENT' ,'Security_Database_ReorganizeFragmented' ,'Error amending indexes for ' + @TableName + ' ' + ERROR_MESSAGE() ); END CATCH END
CLOSE table_cursor;
DEALLOCATE table_cursor;
EXECUTE sp_updatestats;
INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_MESSAGE ) VALUES ( getdate() ,'0' ,'INFO' ,'SQL SERVER AGENT' ,'Security_Database_ReorganizeFragmented' ,'Statistics Updated' ); END

BEGIN SET NOCOUNT ON;
INSERT INTO TD_LOGGING ( LGG_DATETIME ,LGG_THREAD ,LGG_LEVEL ,LGG_CLASS ,LGG_METHOD ,LGG_LINE ,LGG_MESSAGE ,LGG_EXCEPTION ) VALUES ( GETDATE() ,@LggThread ,@LggLevel ,@LggClass ,@LggMethod ,@LggLine ,@LggMessage ,@LggException )
RETURN @@rowcount END

CREATE   PROCEDURE Security_Logging_Read
@LggDatetimeStart DATETIME, @LggDatetimeEnd DATETIME
AS

BEGIN SET NOCOUNT ON;
SELECT CCN_USERNAME as CcnUsername, CCN_LOCKED_FLAG as CcnLockedFlag FROM TD_ACCOUNT INNER JOIN TD_LOGIN ON CCN_ID = LGN_CCN_ID AND CCN_DELETE_FLAG = 0 AND CCN_USERNAME = @CcnUsername AND CCN_LOCKED_FLAG=0 AND LGN_1FA = @Lgn1FA
END

BEGIN SET NOCOUNT ON;
UPDATE TD_LOGIN SET LGN_SESSION = @LgnSession ,LGN_SESSION_EXPIRY = @LgnSessionExpiry FROM TD_LOGIN INNER JOIN TD_ACCOUNT ON CCN_ID = LGN_CCN_ID AND CCN_DELETE_FLAG = 0 AND CCN_USERNAME = @CcnUsername
RETURN @@rowcount END

BEGIN SET NOCOUNT ON;
UPDATE TD_LOGIN SET LGN_SESSION_EXPIRY = @LgnSessionExpiry FROM TD_LOGIN INNER JOIN TD_ACCOUNT ON CCN_ID = LGN_CCN_ID AND CCN_USERNAME=@CcnUsername AND LGN_SESSION IS NOT NULL AND CCN_DELETE_FLAG=0
RETURN @@rowcount END

BEGIN SET NOCOUNT ON;
SELECT CCN_USERNAME AS CcnUsername ,CCN_DISPLAYNAME AS CcnDisplayName ,CCN_EMAIL AS CcnEmail ,CCN_ID AS CcnId FROM TD_ACCOUNT INNER JOIN TD_LOGIN ON CCN_ID = LGN_CCN_ID AND CCN_DELETE_FLAG = 0 AND CCN_LOCKED_FLAG=0 AND LGN_TOKEN_2FA = @LgnToken2Fa AND CCN_USERNAME =@CcnUsername
END

BEGIN SET NOCOUNT ON;
DELETE FROM TD_PERFORMANCE
RETURN 1 END

BEGIN
	SET NOCOUNT ON;

	SELECT PRV_CODE AS PrvCode
		,PRV_VALUE AS PrvValue
	FROM TS_PRIVILEGE
	WHERE (
			@PrvCode IS NULL
			OR (PRV_CODE = @PrvCode)
			)
END

BEGIN
    SET NOCOUNT ON;
    INSERT  INTO TD_API_TRACE (TRC_METHOD, TRC_PARAMS, TRC_IP, TRC_USERAGENT, TRC_USERNAME, TRC_DATETIME, TRC_STARTTIME, TRC_DURATION, TRC_STATUSCODE, TRC_MACHINENAME, TRC_REQUEST_TYPE, TRC_REQUEST_VERB, TRC_ERROR_PATH, TRC_CORRELATION_ID, TRC_JSONRPC_ERROR_CODE, TRC_CONTENT_LENGTH, TRC_REFERER)
    VALUES                   (@TrcMethod, @TrcParams, @TrcIp, @Trcuseragent, @Username, getdate(), @TrcStartTime, @TrcDuration, @TrcStatusCode, @TrcMachineName, @TrcRequestType, @TrcRequestVerb, @TrcErrorPath, @TrcCorrelationID, @TrcJsonRpcErrorCode, @TrcContentLength, @TrcReferrer);
    RETURN 1;
END

CREATE   PROCEDURE Security_Trace_Create
@TrcMethod NVARCHAR (256)=NULL, @TrcParams NVARCHAR (2048)=NULL, @TrcIp VARCHAR (15)=NULL, @TrcUseragent VARCHAR (2048)=NULL, @Username NVARCHAR (256)=NULL, @TrcStartTime DATETIME, @TrcDuration DECIMAL (18, 3), @TrcStatusCode INT, @TrcMachineName VARCHAR (256), @TrcErrorPath VARCHAR (1028)=NULL, @TrcRequestVerb VARCHAR (50), @TrcRequestType VARCHAR (50)=NULL, @TrcCorrelationID VARCHAR (1028), @TrcJsonRpcErrorCode INT=NULL, @TrcContentLength BIGINT=NULL, @TrcReferrer VARCHAR (MAX)=NULL
AS

BEGIN SET NOCOUNT ON;
IF @AuthenticationType IS NULL BEGIN SET @AuthenticationType=@Const_ANY END
SELECT t.TRC_USERNAME AS TrcCcnUsername ,a.PRV_VALUE AS TrcPrvValue ,t.trc_ip AS TrcIp ,t.TRC_METHOD AS TrcMethod ,t.trc_params AS TrcParams ,t.trc_useragent AS TrcUserAgent ,t.trc_datetime AS TrcDatetime FROM td_trace t LEFT OUTER JOIN ( SELECT ac.CCN_USERNAME ,ac.CCN_PRV_ID ,ac.CCN_ID ,pr.PRV_VALUE FROM TD_ACCOUNT ac INNER JOIN TS_PRIVILEGE pr ON ac.CCN_PRV_ID = pr.PRV_ID AND ac.CCN_DELETE_FLAG = 0 WHERE ccn_delete_flag = 0 ) a ON t.TRC_USERNAME = a.CCN_USERNAME WHERE t.TRC_DATETIME >= @StartDate AND t.TRC_DATETIME <= @EndDate AND ( ( @AuthenticationType = @Const_REGISTERED AND a.CCN_USERNAME IS NOT NULL ) OR ( @AuthenticationType = @Const_ANONYMOUS AND a.CCN_USERNAME IS NULL AND t.TRC_USERNAME IS NULL ) OR ( @AuthenticationType = @Const_AUTHENTICATED AND t.TRC_USERNAME IS NOT NULL AND a.CCN_USERNAME IS NULL ) OR (@AuthenticationType = @Const_ANY) ) AND ( @TrcUsername IS NULL OR t.TRC_USERNAME = @TrcUsername ) AND ( @TrcIp IS NULL OR t.TRC_IP = @TrcIp ) ORDER BY t.TRC_DATETIME END

-- ============================================= -- Author: Neil O'Keeffe -- Create date: 02/12/2021 -- Description: Read one or all Subscription Channel -- EXEC Subscription_Channel_Read 'TECHNICAL','sw' -- ============================================= CREATE
PROCEDURE Subscription_Channel_Read
@ChnCode NVARCHAR(256) = NULL ,@LngIsoCode CHAR(2) AS

BEGIN SET NOCOUNT ON; IF @SubscriberUserId IS NULL AND @CcnUsername IS NULL BEGIN RETURN 0; END
DECLARE @UserId AS INT;
IF @SubscriberUserId IS NOT NULL BEGIN SET @UserId = ( SELECT USR_ID FROM TD_USER INNER JOIN TD_SUBSCRIBER ON SBR_USR_ID = USR_ID AND SBR_UID = @SubscriberUserId AND SBR_DELETE_FLAG = 0 ); END ELSE BEGIN SET @UserId = ( SELECT USR_ID FROM TD_USER INNER JOIN TD_ACCOUNT ON CCN_USR_ID = USR_ID AND CCN_USERNAME = @CcnUsername AND CCN_DELETE_FLAG = 0 ); END
IF @UserId IS NULL BEGIN RETURN 0; END
SELECT SQR_ID AS Id ,SQR_SNIPPET_TAGNAME AS TagName ,SQR_SNIPPET_MATRIX AS Matrix ,SQR_SNIPPET_TYPE AS SnippetType ,CAST(decompress(SQR_SNIPPET_QUERY) AS NVARCHAR(MAX)) AS SnippetQueryBase64 ,SQR_FLUID_TIME AS FluidTime ,SQR_SNIPPET_ISOGRAM AS SnippetIsogram FROM TD_USER_QUERY WHERE SQR_USR_ID = @UserId AND SQR_ID = @UserQueryId END
SET ANSI_NULLS ON;

BEGIN SET NOCOUNT ON;
IF @SubscriberUserId IS NULL AND @CcnUsername IS NULL BEGIN RETURN 0; END
DECLARE @UserId AS INT;
IF @SubscriberUserId IS NOT NULL BEGIN SET @UserId = ( SELECT USR_ID FROM TD_USER INNER JOIN TD_SUBSCRIBER ON SBR_USR_ID = USR_ID AND SBR_UID = @SubscriberUserId AND SBR_DELETE_FLAG = 0 ); END ELSE BEGIN SET @UserId = ( SELECT USR_ID FROM TD_USER INNER JOIN TD_ACCOUNT ON CCN_USR_ID = USR_ID AND CCN_USERNAME = @CcnUsername AND CCN_DELETE_FLAG = 0 ); END
IF @UserId IS NULL BEGIN RETURN 0; END
SELECT SQR_ID AS Id ,SQR_SNIPPET_TAGNAME AS TagName ,SQR_SNIPPET_MATRIX AS Matrix ,SQR_SNIPPET_TYPE AS SnippetType ,SQR_FLUID_TIME AS FluidTime FROM TD_USER_QUERY WHERE SQR_USR_ID = @UserId; END
SET ANSI_NULLS ON;

BEGIN SET NOCOUNT ON;
UPDATE TD_SUBSCRIBER SET SBR_DELETE_FLAG = 1 WHERE SBR_UID = @SbrUserId
RETURN @@ROWCOUNT END

BEGIN
	SET NOCOUNT ON;

	SELECT DISTINCT SBR_KEY AS SbrKey
	FROM TD_SUBSCRIBER
	WHERE SBR_DELETE_FLAG = 0;
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here UPDATE TD_SUBSCRIBER SET SBR_KEY = @SbrKey WHERE SBR_UID = @SbrUserId AND SBR_DELETE_FLAG = 0
RETURN @@ROWCOUNT; END

CREATE   PROCEDURE Subscription_Table_Subscription_Delete
@TsbTable NVARCHAR (20), @SubscriberUserId NVARCHAR (256)=NULL, @CcnUsername NVARCHAR (256)=NULL
AS

BEGIN
	SET NOCOUNT ON;



	SELECT DISTINCT MTR_CODE AS MtrCode
		,NULL AS sValue
		,NULL AS sKey
		,NULL AS Attribute
		,NULL FoundValue
		,'Entity' AS KwrSource
	FROM TD_RELEASE
	INNER JOIN (
		SELECT DISTINCT VRN_RLS_ID
		FROM VW_RELEASE_LIVE_NOW
		) AS LIVE_NOW ON RLS_ID = VRN_RLS_ID
	LEFT OUTER JOIN TD_PRODUCT ON RLS_PRC_ID = PRC_ID
	LEFT OUTER JOIN TD_SUBJECT ON PRC_SBJ_ID = SBJ_ID
	LEFT OUTER JOIN TD_THEME ON SBJ_THM_ID=THM_ID
	INNER JOIN TD_MATRIX ON MTR_RLS_ID = RLS_ID
		AND RLS_DELETE_FLAG = 0
		AND MTR_DELETE_FLAG = 0
	INNER JOIN TS_COPYRIGHT ON MTR_CPR_ID = CPR_ID
		AND CPR_DELETE_FLAG = 0


	WHERE 
			(@ThmCode IS NULL 
			OR @ThmCode=THM_CODE)
		AND
			(
			@PrcCode IS NULL
			OR @PrcCode = PRC_CODE
			)
		AND (
			@SbjCode IS NULL
			OR @SbjCode = SBJ_CODE
			)
		AND (
			@MtrCode IS NULL
			OR @MtrCode = MTR_CODE
			)
		AND (
			@MtrOfficialFlag IS NULL
			OR @MtrOfficialFlag = MTR_OFFICIAL_FLAG
			)
		AND (
			@CprCode IS NULL
			OR @CprCode = CPR_CODE
			)
		AND (
			@RlsExceptionalFlag IS NULL
			OR @RlsExceptionalFlag = RLS_EXCEPTIONAL_FLAG
			)
		AND (
			@RlsReservationFlag IS NULL
			OR @RlsReservationFlag = RLS_RESERVATION_FLAG
			)
		AND (
			@RlsArchiveFlag IS NULL
			OR @RlsArchiveFlag = RLS_ARCHIVE_FLAG
			)
		AND (
			@RlsAnalyticalFlag IS NULL
			OR @RlsAnalyticalFlag = RLS_ANALYTICAL_FLAG
			)
		AND (
			@RlsExperimentalFlag IS NULL
			OR @RlsExperimentalFlag = RLS_EXPERIMENTAL_FLAG
			) ;
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT DISTINCT RLS_CODE AS RlsCode FROM TD_MATRIX INNER JOIN TD_RELEASE ON MTR_RLS_ID = RLS_ID AND RLS_DELETE_FLAG=0 AND MTR_DELETE_FLAG=0
INNER JOIN TD_PRODUCT ON PRC_ID = RLS_PRC_ID AND PRC_CODE=@PrcCode AND PRC_DELETE_FLAG = 0 END

BEGIN SET NOCOUNT ON;
DROP TABLE
IF EXISTS #tempKeyword; SELECT DISTINCT krl_value ,KRL_RLS_ID ,KRL_MANDATORY_FLAG ,KRL_SINGULARISED_FLAG INTO #tempKeyword FROM TD_KEYWORD_RELEASE WHERE krl_rls_id = @RlsId;
DELETE TD_KEYWORD_RELEASE WHERE krl_rls_id = @RlsId;
INSERT INTO TD_KEYWORD_RELEASE ( krl_value ,KRL_RLS_ID ,KRL_MANDATORY_FLAG ,KRL_SINGULARISED_FLAG ) SELECT krl_value ,KRL_RLS_ID ,KRL_MANDATORY_FLAG ,KRL_SINGULARISED_FLAG FROM #tempKeyword;
DROP TABLE
IF EXISTS #tempKeyword;END

CREATE   PROCEDURE System_Navigation_Product_ReadAllLanguages
@PrcCode NVARCHAR (32)=NULL, @PrcId INT=NULL
AS

BEGIN SET NOCOUNT ON;
DECLARE @LngIsoId AS INT;
SET @LngIsoId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCode AND LNG_DELETE_FLAG = 0 );
SELECT SbjCode ,SbjValue ,ThmValue ,ThmCode ,sum(( CASE WHEN PRC_ID IS NOT NULL THEN 1 ELSE 0 END )) AS PrcCount FROM ( SELECT SBJ_CODE AS SbjCode ,COALESCE(INNER_SLG.SLG_VALUE, SBJ_VALUE) AS SbjValue ,PRC_ID ,COALESCE(INNER_TLG.TLG_VALUE, THM_VALUE) AS ThmValue ,THM_CODE AS ThmCode FROM TD_SUBJECT INNER JOIN TD_THEME ON SBJ_THM_ID = THM_ID AND SBJ_DELETE_FLAG = 0 AND THM_DELETE_FLAG = 0 LEFT OUTER JOIN ( SELECT SLG_SBJ_ID ,SLG_VALUE FROM TD_SUBJECT_LANGUAGE INNER JOIN TS_LANGUAGE ON SLG_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode ) AS INNER_SLG ON SBJ_ID = INNER_SLG.SLG_SBJ_ID LEFT OUTER JOIN TD_PRODUCT ON PRC_SBJ_ID = SBJ_ID AND PRC_DELETE_FLAG = 0 LEFT OUTER JOIN ( SELECT TLG_THM_ID ,TLG_VALUE FROM TD_THEME_LANGUAGE INNER JOIN TS_LANGUAGE ON TLG_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode ) AS INNER_TLG ON THM_ID = INNER_TLG.TLG_THM_ID WHERE ( ( @SbjCode IS NOT NULL AND [SBJ_CODE] = @SbjCode ) OR @SbjCode IS NULL ) AND ( ( @SbjValue IS NOT NULL AND [SBJ_VALUE] = @SbjValue ) OR @SbjValue IS NULL ) AND SBJ_DELETE_FLAG = 0 ) AS INNER_SELECT GROUP BY SbjCode ,SbjValue ,ThmValue ,ThmCode ORDER BY SbjValue; END

CREATE   PROCEDURE System_Navigation_Subject_ReadAllLanguages
@SbjCode INT=NULL, @SbjId INT=NULL
AS

BEGIN SET NOCOUNT ON;
DECLARE @LngIsoId AS INT;
SET @LngIsoId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCode AND LNG_DELETE_FLAG = 0 );
SELECT ThmCode ,ThmValue ,sum(( CASE WHEN SBJ_ID IS NOT NULL THEN 1 ELSE 0 END )) AS SbjCount FROM ( SELECT SBJ_ID ,COALESCE(INNER_TLG.TLG_VALUE, THM_VALUE) AS ThmValue ,THM_CODE AS ThmCode FROM TD_THEME LEFT OUTER JOIN TD_SUBJECT ON SBJ_THM_ID = THM_ID AND SBJ_DELETE_FLAG = 0 AND THM_DELETE_FLAG = 0
LEFT OUTER JOIN ( SELECT TLG_THM_ID ,TLG_VALUE FROM TD_THEME_LANGUAGE INNER JOIN TS_LANGUAGE ON TLG_LNG_ID = LNG_ID AND LNG_DELETE_FLAG = 0 AND LNG_ISO_CODE = @LngIsoCode ) AS INNER_TLG ON THM_ID = INNER_TLG.TLG_THM_ID WHERE ( ( @ThmCode IS NOT NULL AND [THM_CODE] = @ThmCode ) OR @ThmCode IS NULL ) AND ( ( @ThmValue IS NOT NULL AND [THM_VALUE] = @ThmValue ) OR @ThmValue IS NULL ) AND THM_DELETE_FLAG = 0 ) INNER_SELECT GROUP BY ThmCode ,ThmValue; END

BEGIN SET NOCOUNT ON;
SELECT CPR_CODE AS CprCode ,CPR_VALUE AS CprValue ,CPR_URL AS CprUrl ,coalesce(mtr.mtrCount,0) AS MtrCount FROM TS_COPYRIGHT LEFT JOIN ( SELECT mtrInner.CPR_ID ,count(*) AS mtrCount FROM ( SELECT DISTINCT CPR_ID ,MTR_CODE FROM TS_COPYRIGHT INNER JOIN TD_MATRIX ON CPR_ID =MTR_CPR_ID AND CPR_DELETE_FLAG = 0 AND MTR_DELETE_FLAG = 0 ) mtrInner GROUP BY mtrInner.CPR_ID ) MTR
ON MTR.CPR_ID = TS_COPYRIGHT.CPR_ID WHERE CPR_DELETE_FLAG = 0 AND ( @CprCode IS NULL OR @CprCode = TS_COPYRIGHT.CPR_CODE ) AND ( @CprValue IS NULL OR @CprValue = TS_COPYRIGHT.CPR_VALUE ) AND ( @CprUrl IS NULL OR @CprUrl = TS_COPYRIGHT.CPR_URL ) ORDER BY CPR_VALUE END

BEGIN
	SET NOCOUNT ON;

	SELECT FRM_TYPE AS FrmType
		,FRM_VERSION AS FrmVersion
		,FRM_DIRECTION AS FrmDirection
	FROM TS_FORMAT
	WHERE (
			@FrmType IS NULL
			OR FRM_TYPE = @FrmType
			)
		AND (
			@FrmVersion IS NULL
			OR FRM_VERSION = @FrmVersion
			)
		AND (
			@FrmDirection IS NULL
			OR FRM_DIRECTION = @FrmDirection
			)

	ORDER BY FRM_TYPE ASC
		,FRM_VERSION DESC
END

BEGIN SET NOCOUNT ON;
SELECT LNG_ISO_CODE AS LngIsoCode ,LNG_ISO_NAME AS LngIsoName ,coalesce(mtr.mtrCount,0) AS MtrCount FROM TS_LANGUAGE LEFT JOIN ( SELECT mtrInner.LNG_ID ,count(*) AS mtrCount FROM ( SELECT DISTINCT LNG_ID ,MTR_CODE FROM TS_LANGUAGE INNER JOIN TD_MATRIX ON LNG_ID =MTR_LNG_ID AND LNG_DELETE_FLAG = 0 AND MTR_DELETE_FLAG = 0 ) mtrInner GROUP BY mtrInner.LNG_ID ) MTR
ON MTR.LNG_ID = TS_LANGUAGE.LNG_ID WHERE LNG_DELETE_FLAG = 0 AND ( @LngIsoCode IS NULL OR (LNG_ISO_CODE = @LngIsoCode) ) END

BEGIN SET NOCOUNT ON;
DECLARE @ccnID AS INT;
SET @ccnID = ( SELECT ccn_id FROM TD_ACCOUNT WHERE CCN_DELETE_FLAG = 0 AND CCN_USERNAME = @CcnUsername );
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
DECLARE @LngId INT DECLARE @LngDefaultId INT
SET @LngId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCode AND LNG_DELETE_FLAG = 0 ) SET @LngDefaultId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCodeDefault AND LNG_DELETE_FLAG = 0 )
SELECT DISTINCT rls.RLS_CODE AS RlsCode ,mtr.MTR_CODE AS MtrCode ,grp.GRP_CODE AS GrpCode ,grp.GRP_NAME AS GrpName ,rqs.RQS_CODE AS RqsCode ,rqs.Rqs_Value AS RqsValue ,cmm.CMM_VALUE AS CmmRequestValue ,RLS_EXCEPTIONAL_FLAG AS RlsExceptionalFlag ,WRQ_DATETIME AS WrqDatetime ,DHT_DATETIME AS DhtDatetime ,CCN_USERNAME AS CcnUsername ,coalesce(mtrLng.MTR_TITLE, mtr.MTR_TITLE) MtrTitle FROM TD_MATRIX mtr LEFT JOIN ( SELECT MTR_CODE ,MTR_TITLE ,MTR_RLS_ID FROM TD_MATRIX WHERE MTR_DELETE_FLAG = 0 AND MTR_LNG_ID = @LngId ) mtrLng ON mtr.MTR_CODE = mtrLng.MTR_CODE AND MTR.MTR_RLS_ID = mtrLng.MTR_RLS_ID INNER JOIN TD_RELEASE rls ON mtr.MTR_RLS_ID = rls.RLS_ID AND rls.RLS_DELETE_FLAG = 0 AND mtr.MTR_DELETE_FLAG = 0 INNER JOIN TD_GROUP AS grp ON rls.RLS_GRP_ID = grp.GRP_ID AND grp.GRP_DELETE_FLAG = 0 AND rls.RLS_DELETE_FLAG = 0 INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = rls.RLS_GRP_ID INNER JOIN TD_WORKFLOW_REQUEST AS wrq ON rls.RLS_ID = wrq.WRQ_RLS_ID AND wrq.WRQ_DELETE_FLAG = 0 AND rls.RLS_DELETE_FLAG = 0 INNER JOIN TD_COMMENT cmm ON wrq.WRQ_CMM_ID = cmm.CMM_ID INNER JOIN TM_AUDITING_HISTORY ON WRQ_DTG_ID = DHT_DTG_ID INNER JOIN TS_AUDITING_TYPE ON DHT_DTP_ID = DTP_ID AND DTP_CODE = 'CREATED' INNER JOIN TD_ACCOUNT ON DHT_CCN_ID = CCN_ID AND CCN_DELETE_FLAG = 0 LEFT OUTER JOIN TS_REQUEST AS rqs ON rqs.RQS_ID = wrq.WRQ_RQS_ID AND wrq.WRQ_DELETE_FLAG = 0 LEFT OUTER JOIN TD_WORKFLOW_RESPONSE wrs ON wrq.WRQ_ID = wrs.WRS_WRQ_ID AND wrq.WRQ_DELETE_FLAG = 0 WHERE wrs.WRS_ID IS NULL --We are awaiting response, so this must be null AND ( wrq.WRQ_CURRENT_FLAG IS NULL OR wrq.WRQ_CURRENT_FLAG <> 0 ) AND ( @RlsCode IS NULL OR @RlsCode = rls.RLS_CODE ) AND mtr.MTR_LNG_ID IN ( @LngId ,@LngDefaultId ) END

BEGIN SET NOCOUNT ON;
DECLARE @GroupUserHasAccess TABLE (GRP_ID INT NOT NULL);
INSERT INTO @GroupUserHasAccess EXEC Security_Group_AccessList @CcnUsername
DECLARE @LngId INT DECLARE @LngDefaultId INT
SET @LngId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCode AND LNG_DELETE_FLAG = 0 ) SET @LngDefaultId = ( SELECT LNG_ID FROM TS_LANGUAGE WHERE LNG_ISO_CODE = @LngIsoCodeDefault AND LNG_DELETE_FLAG = 0 )
SELECT DISTINCT * FROM ( SELECT RLS_CODE RlsCode ,mtr.MTR_CODE MtrCode ,GRP_CODE GrpCode ,GRP_NAME GrpName ,RQS_CODE RqsCode ,RQS_VALUE RqsValue ,CCN_USERNAME CcnUsername ,RLS_RESERVATION_FLAG RlsReservationFlag ,RLS_ARCHIVE_FLAG RlsArchiveFlag ,MTR_OFFICIAL_FLAG MtrOfficialFlag ,RLS_LIVE_DATETIME_FROM RlsLiveDatimeFrom ,WRQ_DATETIME WrqDatetime ,max(DHT_DATETIME) DhtDatetime ,coalesce(mtrLng.MTR_TITLE, mtr.MTR_TITLE) MtrTitle FROM TD_MATRIX mtr LEFT JOIN ( SELECT MTR_CODE ,MTR_TITLE ,MTR_RLS_ID FROM TD_MATRIX WHERE MTR_DELETE_FLAG = 0 AND MTR_LNG_ID = @LngId ) mtrLng ON mtr.MTR_CODE = mtrLng.MTR_CODE AND MTR.MTR_RLS_ID = mtrLng.MTR_RLS_ID INNER JOIN TD_RELEASE ON RLS_ID = mtr.MTR_RLS_ID AND RLS_DELETE_FLAG = 0 INNER JOIN VW_RELEASE_PENDING_LIVE ON VPL_RLS_ID = RLS_ID AND VPL_MTR_ID = MTR_ID INNER JOIN @GroupUserHasAccess g ON g.GRP_ID = RLS_GRP_ID INNER JOIN TD_GROUP GRP ON GRP.GRP_ID = RLS_GRP_ID AND GRP_DELETE_FLAG = 0 LEFT JOIN TD_WORKFLOW_REQUEST ON RLS_ID = WRQ_RLS_ID AND WRQ_DELETE_FLAG = 0 LEFT JOIN TS_REQUEST ON WRQ_RQS_ID = RQS_ID LEFT JOIN TD_WORKFLOW_RESPONSE ON WRQ_ID = WRS_WRQ_ID LEFT JOIN TS_RESPONSE ON WRS_RSP_ID = RSP_ID INNER JOIN TD_WORKFLOW_SIGNOFF ON WRS_ID = WSG_WRS_ID INNER JOIN TS_SIGNOFF ON WSG_SGN_ID = SGN_ID AND SGN_CODE = 'APPROVED' LEFT JOIN TM_AUDITING_HISTORY ON WSG_DTG_ID = DHT_DTG_ID INNER JOIN TD_ACCOUNT ON DHT_CCN_ID = CCN_ID AND CCN_DELETE_FLAG = 0 WHERE MTR_DELETE_FLAG = 0 AND ( @RlsCode IS NULL OR @RlsCode = RLS_CODE ) AND mtr.MTR_LNG_ID IN ( @LngId ,@LngDefaultId ) AND WRQ_DATETIME > getdate() GROUP BY RLS_CODE ,mtr.MTR_CODE ,GRP_CODE ,GRP_NAME ,RQS_CODE ,RQS_VALUE ,CCN_USERNAME ,RLS_RESERVATION_FLAG ,RLS_ARCHIVE_FLAG ,MTR_OFFICIAL_FLAG ,RLS_LIVE_DATETIME_FROM ,WRQ_DATETIME ,mtrLng.MTR_TITLE ,mtr.MTR_TITLE ) Q WHERE Q.RlsLiveDatimeFrom = Q.WrqDatetime ORDER BY Q.MtrCode END

BEGIN SET NOCOUNT ON;
SELECT cmm.CMM_VALUE AS CmmValue ,wrq.WRQ_DATETIME AS WrqDatetime ,wrq.WRQ_EXCEPTIONAL_FLAG AS WrqExceptionalFlag ,wrq.WRQ_RESERVATION_FLAG AS WrqReservationFlag ,wrq.WRQ_ARCHIVE_FLAG AS WrqArchiveFlag ,wrq.WRQ_CURRENT_FLAG AS WrqCurrentFlag ,wrq.WRQ_EXPERIMENTAL_FLAG AS WrqExperimentalFlag ,rqs.RQS_VALUE AS RqsValue ,rqs.RQS_CODE AS RqsCode ,CCN_USERNAME As CcnUsername
FROM TD_WORKFLOW_REQUEST wrq INNER JOIN TD_RELEASE rls ON wrq.WRQ_RLS_ID = rls.RLS_ID AND wrq.WRQ_DELETE_FLAG = 0 AND rls.RLS_DELETE_FLAG = 0 INNER JOIN TD_COMMENT cmm ON wrq.WRQ_CMM_ID = cmm.CMM_ID AND cmm.CMM_DELETE_FLAG = 0 INNER JOIN TS_REQUEST rqs ON wrq.WRQ_RQS_ID = rqs.RQS_ID INNER JOIN TM_AUDITING_HISTORY ON wrq.WRQ_DTG_ID=DHT_DTG_ID INNER JOIN TS_AUDITING_TYPE ON DHT_DTP_ID=DTP_ID AND DTP_CODE='CREATED' INNER JOIN TD_ACCOUNT ON DHT_CCN_ID=CCN_ID AND CCN_DELETE_FLAG=0 WHERE rls.RLS_CODE = @RlsCode AND ( @WrqCurrentFlag IS NULL OR wrq.WRQ_CURRENT_FLAG = @WrqCurrentFlag ) END
