using API;
using PxStat.Template;

namespace PxStat.Security.Logging
{
    /// <summary>
    /// Read database logs generated by the application
    /// </summary>
    internal class Logging_BSO_Read : BaseTemplate_Read<Logging_DTO, Logging_VLD_Read>
    {
        /// <summary>
        /// Constructor
        /// </summary>
        /// <param name="request"></param>
        internal Logging_BSO_Read(JSONRPC_API request) : base(request, new Logging_VLD_Read())
        { }

        /// <summary>
        /// Test Privilege
        /// </summary>
        /// <returns></returns>
        override protected bool HasPrivilege()
        {
            return IsPowerUser();
        }

        /// <summary>
        /// Execute
        /// </summary>
        /// <returns></returns>
        protected override bool Execute()
        {
            Logging_ADO adoLogging = new Logging_ADO();

            ADO_readerOutput result;

            result = adoLogging.Read(Ado, DTO.LggDatetimeStart, DTO.LggDatetimeEnd);
            if (!result.hasData)
            {
                return false;
            }

            Response.data = result.data;

            return true;
        }
    }
}